$exe=""
$pathsep=":"
$env_node_path=$env:NODE_PATH
$new_node_path="C:\Users\adsri\Coding\Startup-Templates\CineCarnivalVue\frontend\node_modules\rollup\dist\bin\node_modules;C:\Users\adsri\Coding\Startup-Templates\CineCarnivalVue\frontend\node_modules\rollup\dist\node_modules;C:\Users\adsri\Coding\Startup-Templates\CineCarnivalVue\frontend\node_modules\rollup\node_modules;C:\Users\adsri\Coding\Startup-Templates\CineCarnivalVue\frontend\node_modules;C:\Users\adsri\Coding\Startup-Templates\CineCarnivalVue\node_modules;C:\Users\adsri\Coding\Startup-Templates\node_modules;C:\Users\adsri\Coding\node_modules;C:\Users\adsri\node_modules;C:\Users\node_modules;C:\node_modules;C:\Users\adsri\Coding\Startup-Templates\CineCarnivalVue\frontend\node_modules\.pnpm\node_modules"
if ($PSVersionTable.PSVersion -lt "6.0" -or $IsWindows) {
  # Fix case when both the Windows and Linux builds of Node
  # are installed in the same directory
  $exe=".exe"
  $pathsep=";"
} else {
  $new_node_path="/mnt/c/Users/adsri/Coding/Startup-Templates/CineCarnivalVue/frontend/node_modules/rollup/dist/bin/node_modules:/mnt/c/Users/adsri/Coding/Startup-Templates/CineCarnivalVue/frontend/node_modules/rollup/dist/node_modules:/mnt/c/Users/adsri/Coding/Startup-Templates/CineCarnivalVue/frontend/node_modules/rollup/node_modules:/mnt/c/Users/adsri/Coding/Startup-Templates/CineCarnivalVue/frontend/node_modules:/mnt/c/Users/adsri/Coding/Startup-Templates/CineCarnivalVue/node_modules:/mnt/c/Users/adsri/Coding/Startup-Templates/node_modules:/mnt/c/Users/adsri/Coding/node_modules:/mnt/c/Users/adsri/node_modules:/mnt/c/Users/node_modules:/mnt/c/node_modules:/mnt/c/Users/adsri/Coding/Startup-Templates/CineCarnivalVue/frontend/node_modules/.pnpm/node_modules"
}
if ([string]::IsNullOrEmpty($env_node_path)) {
  $env:NODE_PATH=$new_node_path
} else {
  $env:NODE_PATH="$new_node_path$pathsep$env_node_path"
}

$ret=0
if (Test-Path "$basedir/node$exe") {
  # Support pipeline input
  if ($MyInvocation.ExpectingInput) {
    $input | & "$basedir/node$exe"  "$basedir/../rollup/dist/bin/rollup" $args
